  selector1: SomeType[];

  @select(selector2)
  selector2$: Observable<SomeType[]>;
  selector2: SomeType[];

  @select(mySomeOtherSelector$)
  mySomeOtherSelector$: Observable<SomeotherType>;

  someState: someState;

ngOnInit() {
this.mySomeOtherSelector$
    .pipe(
        filter(somData => isDefinedAndNotNull(someData)),
        mergeMap(data> {
            this.someState = data
            return observableCombineLatest(
                this.selector1$,
