        this.someState2 = someFunctionResult;
        this.selector2 = selector2;
    });

}


  someFunction() {
    return combineLatest(this.someObservable1$, this.someObservable2$).pipe(
      map(([observer1, observer2]) => {
        // ...DO ANYTHING HERE...
        });
      })

