pageCount = (n, p) => {

  if (p <= 1 || p === n || ((n % 2) && p >= (n - 1) ) ) return 0;

  let beginningStart = Math.floor(p / 2);

  let endingStart = Math.floor(n / 2) - beginningStart;

  return Math.min(beginningStart, endingStart);
}

