	for (var i = 0; i < nums.length; i++) {
		if (nums[i] === val) {
			nums.splice(i, 1); // Remember splice() mutates the original array. So on the next iteration, the value of nums.length will be reduced by 1
		}
	}
	return nums.length;
}

