// SOL-1
addEvenFibNum_1 = num => {

    var i = 1,
        j = 2,
        sum = 2;

    while (j < 4000000) {
        var k = j;
        j += i; // Updating j to take up the next Fibonacci Sequence Number by adding its previous number with its current value
        i = k; // Updating i to take up the next consecutive number (which is the previous value of j)


        if ((j % 2) === 0) {
            sum += j;
        }
    }
    return sum;
}

