  let intersection = [];

  arr1 = arr1.sort((a, b) => a - b);
  arr2 = arr2.sort((a, b) => a - b);

  let i = j = 0;

  while (i < arr1.length && j < arr2.length) {

    if (arr1[i] < arr2[j]) i++;
    else if (arr1[i] > arr2[j]) j++;
    else {
      intersection.push(arr1[i]);
      i++, j++;
    }
  }
  return intersection;
}

