// METHOD 1: runtime = O(n) & memory = O(n)
findDup = (list, n) => {
  let obj = {}
  let currentElem

  for (let i = 0; i < list.length; i++) {
    currentElem = list[i]
    if (obj[currentElem]) {
      return currentElem
    } else {
      obj[currentElem] = true
    }
  }
}
