function findSecondLargest (arr) {

	let maxVal = -Infinity;
	let nextMaxVal = -Infinity;

	for (let i = 0; i < arr.length; i++) {
		if (arr[i] > maxVal) {
			nextMaxVal = maxVal;
			maxVal = arr[i];
		} else if (arr[i] < maxVal && arr[i] > nextMaxVal) {
			nextMaxVal = arr[i]; 
		}
	}
	return nextMaxVal;
}


