thirdLargestElem = arr => {

  let first = -Infinity, second = -Infinity, third = -Infinity;

  for (let i of arr) {

    if (first < i) {
      third = second;
      second = first;
      first = i;
    } else if (second < i ) {
      third = second;
      second = i;
    } else if (third < i ) {
      third = i
    }
  }
  return third;
}

