//Alternate-1
/*
‚Äì Declare a hash-map with opening and closing braces
‚Äì Start on the first character and repeat for each character
‚Äì If that character is an opening brace add it to a stack
‚Äì If that character is a closing brace pop from the stack
‚Äì If the popped brace matches the current brace then continue with next iteration of the for loop
‚Äì If the popped element from the stack, which is the last opening brace doesn‚Äôt match the corresponding closing brace in the map, then return false
‚Äì At the end if the stack is not empty then fail  */

let isMatchingBrackets = function (str) {
    let stack = [];
    let map = {
        '(': ')',
        '[': ']',
        '{': '}'
    }

    for (let i = 0; i < str.length; i++) {

        // If character is an opening brace add it to a stack
        if (str[i] === '(' || str[i] === '{' || str[i] === '[' ) {
            stack.push(str[i]);
        }
        //  If that character is a closing brace, pop from the stack, which will also reduce the length of the stack each time a closing bracket is encountered.
        else {
            let last = stack.pop();

            //If the popped element from the stack, which is the last opening brace doesn‚Äôt match the corresponding closing brace in the map, then return false
            if (str[i] !== map[last]) {return false};
        }
    }
    // By the completion of the for loop after checking all the brackets of the str, at the end, if the stack is not empty then fail
        if (stack.length !== 0) {return false};

    return true;
}

